<script>
import countTo from 'vue-count-to';
import {createAnimator} from '~/plugins/animationUtils'

export default {
  name: "index",
  components: {countTo},
  data() {
    return {
      websiteName: process.env.PROJECT_NAME,
      themeColor: process.env.THEME_COLOR,
      sidebarDialog: true,
      labelGroupingDialog: true,
      sidebarList: [],
      labelGroupingList: [],
