      ctnFatalism: 0,
      sumFatalism: 0,

      initialLoading: true,
      selectedArticleLoading: true,
      selectedArticleList: [],

      positionCssRight: true,

      isLoginBinlog: false,
      userInfoLoading: true,

      siteInfoLoading: true,
      siteInfo: true,
      siteOperationTime: null,
      statDataInfoVo: {},
      statDataInfoLoading: false,
      animator: null, // åŠ¨ç”»å™¨å®ä¾‹
