ticleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelLongList\":null,\"likeTimes\":null,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":null,\"collectionTimes\":null,\"numberTimes\":null,\"isPublic\":null,\"auditStatus\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null}}', 0, '', '2023-02-01 20:50:49');
