INSERT INTO `sys_oper_log` VALUES (1645704396416282625, '文章信息（新增文章信息）', 1, 'com.aurora.web.controller.frontDesk.article.ArticleFrontDeskController.add()', 'POST', 1, '道友1645682271283449856', '', '/white/insert/article', '39.144.139.40', ' ', '{\"searchValue\":null,\"createBy\":null,\"createTime\":null,\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":null,\"userId\":null,\"nickname\":null,\"articleTitle\":\"JS实现隔行变色\",\"articleCover\":null,\"articleAbstract\":\"用隔行变色来演示对元素的深一层理解，实现的方法不唯一，尽量使用比较简洁的方式去实现，既方便用户查看也方便修改代码，减少一些不必要的繁琐程序。\",\"articleContent\":\"```html\\n<!DOCTYPE html>\\n<html lang=\\\"en\\\">\\n<head>\\n    <meta charset=\\\"UTF-8\\\">\\n    <meta http-equiv=\\\"X-UA-Compatible\\\" content=\\\"IE=edge\\\">\\n    <meta name=\\\"viewport\\\" content=\\\"width=device-width, initial-scale=1.0\\\">\\n    <title>隔行变色</title>\\n    <style>\\n        * {\\n            margin: 0;\\n            padding: 0;\\n        }\\n        ul {\\n            list-style: none;\\n        }\\n        #box {\\n            margin: 10px auto;\\n            width: 500px;\\n        }\\n        #box li {\\n            line-height: 50px;\\n            border-bottom: 1px dashed #999;\\n        }\\n    </style>\\n</head>\\n<body>\\n    <ul id=\\\"box\\\">\\n        <li>我是第1个LI</li>\\n        <li>我是第2个LI</li>\\n        <li>我是第3个LI</li>\\n        <li>我是第4个LI</li>\\n        <li>我是第5个LI</li>\\n    </ul>\\n    <!-- IMPORT JS -->\\n    <script>\\n        var box = document.getElementById(\'box\');\\n        var boxList = box.getElementsByTagName(\'li\');\\n        /*\\n         * 自定义属性编程思想：\\n         *    前期把一些值存储到元素的自定义属性上，后期需要用到的时候，直\\n         * 接从属性上获取到这些值即可 \\n         */\\n        for (var i = 0;i < boxList.length;i++) {\\n            // boxList[i]:当前循环要操作的这个LI\\n            var bg = i % 2 ===0 ? \'white\' : \'pink\';\\n            boxList[i].style.backgroundColor = bg;\\n\\n            // 设置自定义属性myOriginBg用来存储每一个LI的背景\\n            boxList[i].myOriginBg = bg;\\n\\n            boxList[i].onmouseover = function () {\\n                // this 当前操作的这个LI\\n                this.style.backgroundColor = \'lightblue\';\\n            }\\n            boxList[i].onmouseout = function () {\\n                this.style.backgroundColor = this.myOriginBg;\\n            }\\n        }\\n    </script>\\n</body>\\n</html>\\n\\n```\\n\",\"theme\":\"androidstudio\",\"type\":', '{\"code\":200,\"msg\":\"操作成功\",\"data\":{\"uuid\":null,\"fabulousCount\":0,\"fansFabulousCount\":0,\"collectionCount\":0,\"followCount\":0,\"fansFollowCount\":0,\"commentCount\":0,\"fansCommentCount\":0,\"articleCount\":0,\"specialColumnCount\":0,\"fansSpecialColumn\":0,\"circleCount\":0,\"id\":12,\"userId\":null,\"articleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelNameList\":null,\"labelLongList\":null,\"likeTimes\":null,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":null,\"collectionTimes\":null,\"numberTimes\":null,\"isPublic\":null,\"auditState\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"heatWeight\":null,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null,\"isFollow\":false,\"buttonLoading\":false}}', 0, '', '2023-04-11 16:24:38');
