INSERT INTO `sys_oper_log` VALUES (1645709858914234370, '文章信息（新增文章信息）', 1, 'com.aurora.web.controller.frontDesk.article.ArticleFrontDeskController.add()', 'POST', 1, '道友1645682271283449856', '', '/white/insert/article', '39.144.139.40', ' ', '{\"searchValue\":null,\"createBy\":null,\"createTime\":null,\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":null,\"userId\":null,\"nickname\":null,\"articleTitle\":\"选项卡(html+css+js)\",\"articleCover\":null,\"articleAbstract\":\"选项卡作为一个小型模块，在项目中的使用也是极为广泛，初学者或基础知识不强的IT小白可以通过反复的练习选项卡模块，熟练掌握一些函数的使用、函数的封装和对索引的深一层理解。\",\"articleContent\":\"```html\\n<!DOCTYPE html>\\n<html lang=\\\"en\\\">\\n<head>\\n    <meta charset=\\\"UTF-8\\\">\\n    <meta http-equiv=\\\"X-UA-Compatible\\\" content=\\\"IE=edge\\\">\\n    <meta name=\\\"viewport\\\" content=\\\"width=device-width, initial-scale=1.0\\\">\\n    <title>隔行变色</title>\\n    <style>\\n        * {\\n            margin: 0;\\n            padding: 0;\\n        }\\n        \\n    </style>\\n</head>\\n<body>\\n    <button value=\\\"按钮1\\\" >按钮1</button>\\n    <button value=\\\"按钮2\\\" >按钮2</button>\\n    <button value=\\\"按钮3\\\" >按钮3</button>\\n    <button value=\\\"按钮4\\\" >按钮4</button>\\n    <button value=\\\"按钮5\\\" >按钮5</button>\\n    <!-- IMPORT JS -->\\n    <script>\\n        var btnList = document.getElementsByTagName(\'button\');\\n        for ( var i = 0;i < btnList.length;i++) {\\n            // btnList: 当前循环下我们要操作的那个button(I变量储\\n            // 存的值是我们需要获取指定元素的索引)\\n            // 在循环给每个button绑定点击事件的时候，我们给每一个button(元素对象)\\n            // 设置一个自定义属性MY-INDEX，属性值存储的是当前button的索引。\\n            btnList[i].myIndex = i;\\n            btnList[i].onclick = function () {\\n            // 我想用的是点击这个button的索引，但是I不是\\n            // THIS是当前点击的这个元素button；THIS.MY-INDEX获取的就是\\n            // 之前绑定在元素自定义属性上的索引值；\\n                alert(this.myIndex);\\n            }\\n        }\\n    </script>\\n</body>\\n</html>\\n\\n\\n```\\n```css\\n*{\\n    margin: 0;\\n    padding: 0;\\n}\\nul  {\\n    list-style: none;\\n}\\n#tabBox {\\n    box-sizing: border-box;\\n    margin: 20px auto;\\n    width: 500px;\\n}\\n#navBox {\\n    /* 只要把容器设置为flex弹性盒子容\\n    器，那么容器中的子元素默认在容器主\\n    轴(X轴)水平靠左排列 */\\n    display: flex;\\n    position: relative;\\n    top: 1px;\\n}\\n#navBox li {\\n    box-sizing: border-box;\\n    margin-right: 10px;\\n    line-height: 20px;\\n    padding: 0 10px;\\n   ', '{\"code\":200,\"msg\":\"操作成功\",\"data\":{\"uuid\":null,\"fabulousCount\":0,\"fansFabulousCount\":0,\"collectionCount\":0,\"followCount\":0,\"fansFollowCount\":0,\"commentCount\":0,\"fansCommentCount\":0,\"articleCount\":0,\"specialColumnCount\":0,\"fansSpecialColumn\":0,\"circleCount\":0,\"id\":13,\"userId\":null,\"articleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelNameList\":null,\"labelLongList\":null,\"likeTimes\":null,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":null,\"collectionTimes\":null,\"numberTimes\":null,\"isPublic\":null,\"auditState\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"heatWeight\":null,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null,\"isFollow\":false,\"buttonLoading\":false}}', 0, '', '2023-04-11 16:46:21');
