INSERT INTO `sys_oper_log` VALUES (1646765280277348354, '网站文档', 2, 'com.aurora.web.controller.business.configure.ToSiteFileController.edit()', 'PUT', 1, 'aurora', '', '/site/file', '39.144.137.193', '四川省 成都市', '{\"searchValue\":null,\"createBy\":null,\"createTime\":\"2023-04-14 14:35:04\",\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":2,\"title\":\"前端技术文档\",\"content\":\"## 前言\\n\\nAurora为社区论坛类型项目，项目为前后端分离，\\n**” 努力的意义就是，以后的日子里，放眼望去，都是自己喜欢的人和事“**\\n\\n## 项目使用技术\\n\\n### 前端\\n基于若依二次开发\\n> 若依官网：http://www.ruoyi.vip/\\n\\n**前端开发框架 Vue、Element UI**\\n\\n> Vue官网：https://cn.vuejs.org/\\n> Element 官网：https://element.eleme.cn/#/zh-CN\\n\\n**vue router 路由管理**\\n\\n**Axios 异步请求**\\n\\n**Sass**\\n\\nAntv G2Plot 数据可视化\\n> 官网：https://g2plot.antv.antgroup.com/\\n\\nMavon-Editor 富文本编辑器\\n> 官网：https://gitee.com/kongfanqun/mavonEditor\\n\\n## 项目架构\\n\\n### 技术选型架构图\\n\\n![image.png](https://zsh-aurora.oss-cn-beijing.aliyuncs.com/aurora/2023/04/14/a01880dec42743a289ca3af239f20893.png)\\n\\n## 项目仓库地址\\n\\nhttps://gitee.com/jade_purple/aurora\\n\\n![](https://cdn.nlark.com/yuque/0/2022/png/2145958/1664630401335-c10c1409-2727-487d-ae9f-331570a04d39.png)\",\"state\":0,\"type\":\"网站文档\",\"theme\":\"androidstudio\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:40:13');
INSERT INTO `sys_oper_log` VALUES (1646765427237371906, '网站文档', 2, 'com.aurora.web.controller.business.configure.ToSiteFileController.edit()', 'PUT', 1, 'aurora', '', '/site/file', '39.144.137.193', '四川省 成都市', '{\"searchValue\":null,\"createBy\":null,\"createTime\":\"2023-04-14 14:34:31\",\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":1,\"title\":\"后端技术文档\",\"content\":\"## 前言\\n\\nAurora为社区论坛类型项目，项目为前后端分离，\\n**” 努力的意义就是，以后的日子里，放眼望去，都是自己喜欢的人和事“**\\n\\n## 项目使用技术\\n\\n### 后端\\n\\n基于若依二次开发\\n> 若依官网：http://www.ruoyi.vip/\\n\\n**后端开发框架 Spring Boot**   \\n\\n**容器框架 Undertow 基于 XNIO 的高性能容器** \\n\\n> 文档地址：https://undertow.io/undertow-docs/undertow-docs-2.0.0/index.html\\n\\n**权限认证框架 Sa-Token、Jwt 支持多终端认证系统**\\n\\n>  Sa-Token官网：https://sa-token.dev33.cn/\\n\\n**关系数据库 MySQL8.0**\\n\\n**缓存数据库 Redis**\\n\\n**数据库框架 Mybatis-Plus 快速 CRUD 增加开发效率**   \\n> Mybatis-Plus官网：https://baomidou.com/\\n\\n**数据库框架 p6spy 更强劲的 SQL 分析**\\n\\n**序列化框架 Jackson 统一使用 jackson 高效可靠**\\n\\n**Redis客户端 Redisson 性能强劲、API丰富**\\n\\n**分布式限流 Redisson 全局、请求IP、集群ID 多种限流**\\n\\n**分布式幂等 Redisson 拦截重复提交**\\n\\n> Redisson官网：https://redisson.org/\\n\\n**分布式锁 Lock4j 注解锁、工具锁** \\n\\n**分布式任务调度 Xxl-Job 高性能 高可靠 易扩展**\\n\\n>  Xxl-Job 官网：https://www.xuxueli.com/xxl-job/\\n\\n\\n**分布式云存储 阿里云oss**\\n\\n**监控框架 SpringBoot-Admin 全方位服务监控**\\n\\n> SpringBoot-Admin官网：https://github.com/codecentric/spring-boot-admin/\\n\\n**校验框架 Validation 增强接口安全性 严谨性**\\n\\n**Excel框架 Alibaba EasyExcel 性能优异 扩展性强**\\n\\n> Alibaba EasyExcel 官网：https://www.yuque.com/easyexcel/doc/easyexcel\\n\\n**文档框架 knife4j 美化接口文档**\\n\\n**工具类框架 Hutool、Lombok 减少代码冗余 增加安全性**\\n\\n> Hutool官网：https://www.hutool.cn/docs/#/\\n\\n**部署方式 Docker 容器编排 一键部署业务集群**\\n\\n> Docker 官网：https://docs.docker.com/engine/install/centos/\\n\\n## 项目架构\\n\\n### 技术选型架构图\\n\\n![img](https://cdn.nlark.com/yuque/0/2022/png/2145958/1664626482035-431b2d13-2267-44de-9cb9-47ae5e2dff85.png)\\n\\n## 项目仓库地址\\n\\nhttps://gitee.com/jade_purple/aurora\\n\\n![](https://cdn.nlark.com/yuque/0/2022/png/2145958/1664630401335-c10c1409-2727-487d-ae9f-331570a04d39.png)\",\"state\":0,\"type\":\"网站文档\",\"theme\":\"androidstudio\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:40:48');
