>\\n            <version>1.1.21</version>\\n        </dependency>\\n```\\n## 2、配置数据源\\n```yml\\n#  数据源配置\\nspring:\\n  datasource:\\n    type: com.alibaba.druid.pool.DruidDataSource\\n    dynamic:\\n      primary: test_1 #设置默认的数据源或者数据源组,默认值即为master\\n      strict: true #严格匹配数据源,默认false. true未匹配到指定数据源时抛异常,false使用默认数据源\\n      datasou', '{\"code\":200,\"msg\":\"操作成功\",\"data\":{\"uuid\":null,\"fabulousCount\":0,\"fansFabulousCount\":0,\"collectionCount\":0,\"followCount\":0,\"fansFollowCount\":0,\"commentCount\":0,\"fansCommentCount\":0,\"articleCount\":0,\"specialColumnCount\":0,\"fansSpecialColumn\":0,\"circleCount\":0,\"id\":19,\"userId\":null,\"articleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelNameList\":null,\"labelLongList\":null,\"likeTimes\":null,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":null,\"collectionTimes\":null,\"numberTimes\":null,\"isPublic\":null,\"auditState\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"heatWeight\":null,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null,\"isFollow\":false,\"buttonLoading\":false}}', 0, '', '2023-04-19 13:13:15');
