INSERT INTO `sys_oper_log` VALUES (1654039607276257281, '名言信息', 1, 'com.aurora.web.controller.frontDesk.dictum.FdDictumInfoController.add()', 'POST', 1, '道友1636168030713155584', '', '/frontDesk/dictum/info', '39.144.137.231', '四川省 成都市', '{\"searchValue\":null,\"createBy\":null,\"createTime\":null,\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":\"1654039605632090114\",\"uid\":null,\"content\":\"我当时太不懂事，还不知道怎样去爱它……我早应该猜出在它那个令人爱怜的花招后面所隐藏的绵绵情意。\",\"groupId\":1,\"albumId\":4,\"albumName\":null,\"label\":\"小王子,童话\",\"helpSum\":null,\"commentSum\":null,\"picture\":null,\"author\":null,\"worksName\":\"小王子\",\"dictumState\":1,\"state\":null,\"nickname\":null,\"avatar\":null,\"roleId\":null,\"location\":null}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-05-04 16:25:48');
INSERT INTO `sys_oper_log` VALUES (1654039731419267073, '名言信息', 1, 'com.aurora.web.controller.frontDesk.dictum.FdDictumInfoController.add()', 'POST', 1, '道友1636168030713155584', '', '/frontDesk/dictum/info', '39.144.137.231', '四川省 成都市', '{\"searchValue\":null,\"createBy\":null,\"createTime\":null,\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":\"1654039728961404930\",\"uid\":null,\"content\":\"你对我来说，只不过是一个很普通的小男孩，而且和其他千千万万个小男孩没什么两样。就是说，我不需要你，你也不会需要我。对你来说，也是同样的道理。我也只不过是一只很普通的狐狸，也和其他千千万万只狐狸没有什么区别。但是，你一旦把我驯服了，我们彼此之间就会建立起不可缺少的关系，我们就会相互依恋，难舍难分——你会需要我，我也会需要你。在我心里，你就是这个世界上我唯一的人；在你心里，我也是你在这个世界上唯一的狐狸了……\",\"groupId\":1,\"albumId\":4,\"albumName\":null,\"label\":\"小王子\",\"helpSum\":null,\"commentSum\":null,\"picture\":null,\"author\":null,\"worksName\":\"小王子\",\"dictumState\":1,\"state\":null,\"nickname\":null,\"avatar\":null,\"roleId\":null,\"location\":null}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-05-04 16:26:17');
