axFileSize>10MB</maxFileSize>\\n            </timeBasedFileNamingAndTriggeringPolicy>\\n        </rollingPolicy>\\n\\n        <!--输出格式-->\\n        <encoder class=\\\"ch.qos.logback.classic.encoder.PatternLayoutEncoder\\\">\\n            <!--格式化输出：%d表示日期，%thread表示线程名，%-5level：级别从左显示5个字符宽度%msg：日志消息，%n是换行符-->\\n            <pattern>%d{yyyy-MM-dd HH:m', '{\"code\":200,\"msg\":\"操作成功\",\"data\":{\"uuid\":null,\"fabulousCount\":0,\"fansFabulousCount\":0,\"collectionCount\":0,\"followCount\":0,\"fansFollowCount\":0,\"commentCount\":0,\"fansCommentCount\":0,\"articleCount\":0,\"specialColumnCount\":0,\"fansSpecialColumn\":0,\"circleCount\":0,\"albumCount\":0,\"dictumCount\":0,\"id\":43,\"userId\":null,\"articleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelNameList\":null,\"labelLongList\":null,\"likeTimes\":0,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":0,\"collectionTimes\":0,\"numberTimes\":0,\"isPublic\":null,\"auditState\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"heatWeight\":0,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null,\"isFollow\":false,\"buttonLoading\":false}}', 0, '', '2024-06-18 14:17:55');
