INSERT INTO `sys_oper_log` VALUES (1803073783849213953, '文章信息（新增文章信息）', 1, 'com.aurora.web.controller.frontDesk.article.UserArticleController.add()', 'POST', 1, '美团干饭人', '', '/user/insert/article', '182.148.200.53', '四川省 成都市', '{\"searchValue\":null,\"createBy\":null,\"createTime\":\"2024-06-18 14:17:54\",\"updateBy\":null,\"updateTime\":null,\"params\":{},\"id\":43,\"userId\":\"1636168030713155584\",\"nickname\":null,\"articleTitle\":\"logback.xml 日志配置\",\"articleCover\":null,\"articleAbstract\":\"需求，将日志统一保存到里面，对于特殊模块日志分开保存配置设置存储路径变量控制台输出设置输出格式格式化输出：表示日期，表示线程名，：级别从左显示个字符宽度：日志消息，是换行符设置编码文件输出时间窗口滚动日志名指定最新的文件名，其他文件名使用文\",\"articleContent\":\"## 需求，将日志统一保存到info.log里面，对于特殊模块日志分开保存\\n\\n### logback.xml配置\\n```xml\\n<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<configuration debug=\\\"false\\\">\\n    <!--设置存储路径变量-->\\n    <property name=\\\"LOG_HOME\\\" value=\\\"./log\\\"/>\\n\\n    <!--控制台输出appender-->\\n    <appender name=\\\"console\\\" class=\\\"ch.qos.logback.core.ConsoleAppender\\\">\\n        <!--设置输出格式-->\\n        <encoder class=\\\"ch.qos.logback.classic.encoder.PatternLayoutEncoder\\\">\\n            <!--格式化输出：%d表示日期，%thread表示线程名，%-5level：级别从左显示5个字符宽度%msg：日志消息，%n是换行符-->\\n            <pattern>%d{yyyy-MM-dd HH:mm:ss.SSS} [%thread] %-5level %logger{50} - %msg%n</pattern>\\n            <!--设置编码-->\\n            <charset>UTF-8</charset>\\n        </encoder>\\n    </appender>\\n\\n    <!--文件输出,时间窗口滚动-->\\n    <appender name=\\\"test\\\" class=\\\"ch.qos.logback.core.rolling.RollingFileAppender\\\">\\n        <!--日志名,指定最新的文件名，其他文件名使用FileNamePattern -->\\n        <File>${LOG_HOME}/info.log</File>\\n        <!--文件滚动模式-->\\n        <rollingPolicy class=\\\"ch.qos.logback.core.rolling.TimeBasedRollingPolicy\\\">\\n            <!--日志文件输出的文件名,可设置文件类型为gz,开启文件压缩-->\\n            <FileNamePattern>${LOG_HOME}/timeFile/info.%d{yyyy-MM-dd}.%i.log.gz</FileNamePattern>\\n            <!--日志文件保留天数-->\\n            <MaxHistory>30</MaxHistory>\\n            <!--按大小分割同一天的-->\\n            <timeBasedFileNamingAndTriggeringPolicy class=\\\"ch.qos.logback.core.rolling.SizeAndTimeBasedFNATP\\\">\\n                <maxFileSize>10MB</maxFileSize>\\n            </timeBasedFileNamingAndTriggeringPolicy>\\n        </rollingPolicy>\\n\\n        <!--输出格式-->\\n        <encoder class=\\\"ch.qos.logback.classic.encoder.PatternLayout', '{\"code\":200,\"msg\":\"操作成功\",\"data\":{\"uuid\":null,\"fabulousCount\":0,\"fansFabulousCount\":0,\"collectionCount\":0,\"followCount\":0,\"fansFollowCount\":0,\"commentCount\":0,\"fansCommentCount\":0,\"articleCount\":0,\"specialColumnCount\":0,\"fansSpecialColumn\":0,\"circleCount\":0,\"albumCount\":0,\"dictumCount\":0,\"id\":43,\"userId\":null,\"articleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelNameList\":null,\"labelLongList\":null,\"likeTimes\":0,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":0,\"collectionTimes\":0,\"numberTimes\":0,\"isPublic\":null,\"auditState\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"heatWeight\":0,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null,\"isFollow\":false,\"buttonLoading\":false}}', 0, '', '2024-06-18 22:34:24');
