lue\\n&nbsp;  }) */\\n&nbsp;​\\n&nbsp;​\\n&nbsp; &nbsp;// 计算属性——既读取又修改\\n&nbsp; &nbsp;let fullName = computed({\\n&nbsp; &nbsp; &nbsp;// 读取\\n&nbsp; &nbsp; &nbsp;get(){\\n&nbsp; &nbsp; &nbsp; &nbsp;return firstName.value + \'-\' + lastName.value\\n&nbsp; &nbsp;  },\\n&nbsp; &nbsp; &nbsp;// 修改\\n&nbsp; &nbsp; &nbsp;set(val){\\n&nbsp; &nbsp; &nbsp; &nbsp;console.log(\'有人修改了fullName\',val)\\n&nbsp; ', '{\"code\":200,\"msg\":\"操作成功\",\"data\":{\"uuid\":null,\"fabulousCount\":0,\"fansFabulousCount\":0,\"collectionCount\":0,\"followCount\":0,\"fansFollowCount\":0,\"commentCount\":0,\"fansCommentCount\":0,\"articleCount\":0,\"specialColumnCount\":0,\"fansSpecialColumn\":0,\"circleCount\":0,\"albumCount\":0,\"dictumCount\":0,\"id\":54,\"userId\":null,\"articleTitle\":null,\"articleCover\":null,\"articleAbstract\":null,\"articleContent\":null,\"theme\":null,\"type\":null,\"reprintUrl\":null,\"state\":null,\"updateId\":null,\"specialId\":null,\"collectionId\":null,\"collectionRecordId\":null,\"groupingId\":null,\"groupingName\":null,\"labelId\":null,\"labelList\":null,\"labelNameList\":null,\"labelLongList\":null,\"likeTimes\":0,\"fabulousUserSet\":null,\"isCollection\":null,\"collectionRId\":null,\"commentTimes\":0,\"collectionTimes\":0,\"numberTimes\":0,\"isPublic\":null,\"auditState\":null,\"auditTime\":null,\"updateTime\":null,\"createTime\":null,\"heatWeight\":0,\"occupation\":null,\"username\":null,\"nickname\":null,\"avatar\":null,\"isFollow\":false,\"buttonLoading\":false}}', 0, '', '2024-11-13 22:51:46');
